import React, { useContext, useEffect, useState } from 'react'
import { ShopContext } from '../context/ShopContext';
import Title from './Title';
import Productitems from './Productitems';

const LatestCollection = () => {
    const  {products} =useContext(ShopContext);
    const [latestProducts,setLatestProducts]= useState([]);
    useEffect(()=>{
      setLatestProducts(products.slice(0,100));
    },[])
  return (
    <div className='my-10'>
      <div className="text-center py-8 text-3xl">
      <Title text1={"LATEST"} text2={"COL"}/>
      <p className="w-3/4 m-auto text-xs sm:text-sm  md:text-base text-gray-600">Lorem, ipsum dolor sit amet consectetur adipisicing elit. Praesentium, neque deleniti. Atque maiores labore quam neque ratione itaque dolor delectus, adipisci perferendis magnam quibusdam nemo, distinctio quas similique blanditiis porro, vel eveniet! Rerum laudantium, voluptate deserunt, eius, exercitationem aliquam voluptas ut asperiores accusantium numquam magnam placeat nam beatae delectus ullam!!</p>
    </div>
    {/* rendering the product */}
    <div className=' grid grid-cols-2 sm-grid-cols-3 md:grid-cols-4 lg:grid-col-5 gap-4 gap-y-3'>
      {
      latestProducts.map((item,index)=>(
        <Productitems key={index} id ={ item._id} image={item.image} name={item.name} price={item.price}/>
      ))
    }
    </div> 

    </div> 
  )
}

export default LatestCollection
